function checkProjectileHit(projectile, hitCheck) {
    if (!projectile.parentNode) {
        clearInterval(hitCheck);
        return;
    }
    
    let projRect = projectile.getBoundingClientRect();
    let gameArea = document.getElementById('gameArea');
    let gameRect = gameArea.getBoundingClientRect();
    
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–æ–ø–∞–¥–∞–Ω–∏–µ –≤ —Ü–µ–Ω—Ç—Ä –∏–≥—Ä–æ–≤–æ–π –æ–±–ª–∞—Å—Ç–∏ (—É—Å–ª–æ–≤–Ω–æ "–∏–≥—Ä–æ–∫")
    let centerX = gameRect.left + gameRect.width / 2;
    let centerY = gameRect.top + gameRect.height / 2;
    
    let distance = Math.sqrt(
        Math.pow(projRect.left - centerX, 2) + 
        Math.pow(projRect.top - centerY, 2)
    );
    
    // –ï—Å–ª–∏ —Å–Ω–∞—Ä—è–¥ –±–ª–∏–∑–∫–æ –∫ —Ü–µ–Ω—Ç—Ä—É (–ø–æ–ø–∞–¥–∞–Ω–∏–µ)
    if (distance < 50) {
        clearInterval(hitCheck);
        projectile.remove();
        
        // MASS INFECTION –°–¢–ê–ù –ù–ê 5 –°–ï–ö–£–ù–î! üíÄ
        massInfectionStun();
    }
}

function checkWallHit(wall, hitCheck) {
    if (!wall.parentNode) {
        clearInterval(hitCheck);
        return;
    }
    
    let wallRect = wall.getBoundingClientRect();
    let gameArea = document.getElementById('gameArea');
    let gameRect = gameArea.getBoundingClientRect();
    
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–µ—Ä–µ—Å–µ—á–µ–Ω–∏–µ —Å —Ü–µ–Ω—Ç—Ä–∞–ª—å–Ω–æ–π –æ–±–ª–∞—Å—Ç—å—é (–∏–≥—Ä–æ–∫)
    let centerX = gameRect.left + gameRect.width / 2;
    let centerY = gameRect.top + gameRect.height / 2;
    let playerSize = 100; // –†–∞–∑–º–µ—Ä "—Ö–∏—Ç–±–æ–∫—Å–∞" –∏–≥—Ä–æ–∫–∞
    
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–µ—Ä–µ—Å–µ—á–µ–Ω–∏–µ –ø—Ä—è–º–æ—É–≥–æ–ª—å–Ω–∏–∫–æ–≤
    if (wallRect.left < centerX + playerSize/2 && 
        wallRect.right > centerX - playerSize/2 &&
        wallRect.top < centerY + playerSize/2 && 
        wallRect.bottom > centerY - playerSize/2) {
        
        clearInterval(hitCheck);
        
        // –≠—Ñ—Ñ–µ–∫—Ç –ø–æ–ø–∞–¥–∞–Ω–∏—è
        wall.style.background = 'linear-gradient(45deg, #ff0000, #ff0000)';
        wall.style.boxShadow = '0 0 50px #ff0000';
        
        setTimeout(() => {
            if (wall.parentNode) {
                wall.remove();
            }
        }, 200);
        
        // MASS INFECTION –°–¢–ê–ù –ù–ê 5 –°–ï–ö–£–ù–î! üíÄ
        massInfectionStun();
    }
}

