function moveBossArc() {
    let boss = document.getElementById('boss');
    if (!boss || bossHP <= 0) return;
    
    let currentX = parseInt(boss.style.left) || 360;
    let currentY = parseInt(boss.style.top) || 260;
    
    // –°–ª—É—á–∞–π–Ω–∞—è —Ü–µ–ª–µ–≤–∞—è —Ç–æ—á–∫–∞
    let targetX = Math.random() * 720;
    let targetY = Math.random() * 520;
    
    // –î–£–ì–û–í–û–ï –î–í–ò–ñ–ï–ù–ò–ï! üåô
    let midX = (currentX + targetX) / 2 + (Math.random() - 0.5) * 200;
    let midY = (currentY + targetY) / 2 + (Math.random() - 0.5) * 200;
    
    // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –ø—Ä–æ–º–µ–∂—É—Ç–æ—á–Ω—É—é —Ç–æ—á–∫—É
    midX = Math.max(0, Math.min(720, midX));
    midY = Math.max(0, Math.min(520, midY));
    
    // CSS –∞–Ω–∏–º–∞—Ü–∏—è –ø–æ –∫—Ä–∏–≤–æ–π –ë–µ–∑—å–µ!
    boss.style.transition = `all ${bossSpeed/1000}s cubic-bezier(0.25, 0.46, 0.45, 0.94)`;
    
    // –°–Ω–∞—á–∞–ª–∞ –∫ –ø—Ä–æ–º–µ–∂—É—Ç–æ—á–Ω–æ–π —Ç–æ—á–∫–µ
    setTimeout(() => {
        boss.style.left = midX + 'px';
        boss.style.top = midY + 'px';
        
        // –ü–æ—Ç–æ–º –∫ —Ñ–∏–Ω–∞–ª—å–Ω–æ–π
        setTimeout(() => {
            boss.style.left = targetX + 'px';
            boss.style.top = targetY + 'px';
        }, bossSpeed/3);
    }, 50);
    
    // –°–ª—É—á–∞–π–Ω—ã–π —Å—Ç–∞–Ω (—Ç–µ–ø–µ—Ä—å —á–∞—â–µ!)
    if (bossPhases.dark && Math.random() < 0.4) {
        stunPlayer();
    }
}

