let heartKeys = {};

function setupHeartControls() {
    document.addEventListener('keydown', (e) => {
        if (!phase2Active) return; // –í–ê–ñ–ù–û!
        heartKeys[e.key.toLowerCase()] = true;
        e.preventDefault();
    });
    
    document.addEventListener('keyup', (e) => {
        if (!phase2Active) return; // –í–ê–ñ–ù–û!
        heartKeys[e.key.toLowerCase()] = false;
    });
    
    // –î–≤–∏–∂–µ–Ω–∏–µ —Å–µ—Ä–¥—Ü–∞
    setInterval(() => {
        if (!phase2Active || !playerHeart || playerHeartHP <= 0) return; // –í–ê–ñ–ù–û!
        
        let speed = 5;
        let currentX = parseInt(playerHeart.style.left);
        let currentY = parseInt(playerHeart.style.top);
        
        if (heartKeys['w'] || heartKeys['arrowup']) {
            currentY = Math.max(0, currentY - speed);
        }
        if (heartKeys['s'] || heartKeys['arrowdown']) {
            currentY = Math.min(570, currentY + speed);
        }
        if (heartKeys['a'] || heartKeys['arrowleft']) {
            currentX = Math.max(0, currentX - speed);
        }
        if (heartKeys['d'] || heartKeys['arrowright']) {
            currentX = Math.min(770, currentX + speed);
        }
        
        playerHeart.style.left = currentX + 'px';
        playerHeart.style.top = currentY + 'px';
        
    }, 16);
}


