function checkPhase2Hit(projectile, hitCheck, damage) {
    if (!projectile.parentNode || !playerHeart || !phase2Active) {
        clearInterval(hitCheck);
        return;
    }
    
    let projRect = projectile.getBoundingClientRect();
    let heartRect = playerHeart.getBoundingClientRect();
    
    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–µ—Ä–µ—Å–µ—á–µ–Ω–∏—è
    if (projRect.left < heartRect.right && 
        projRect.right > heartRect.left &&
        projRect.top < heartRect.bottom && 
        projRect.bottom > heartRect.top) {
        
        clearInterval(hitCheck);
        
        // –£–±–∏—Ä–∞–µ–º —Å–Ω–∞—Ä—è–¥
        if (projectile.parentNode) {
            projectile.remove();
        }
        
        //console.log(`–ü–æ–ø–∞–¥–∞–Ω–∏–µ! –£—Ä–æ–Ω: ${damage}`); // DEBUG
        
        // –£—Ä–æ–Ω —Å–µ—Ä–¥—Ü—É!
        damageHeart(damage);
    }
}

function damageHeart(damage) {
    // –ü–†–û–í–ï–†–Ø–ï–ú —á—Ç–æ –∏–≥—Ä–∞ –∞–∫—Ç–∏–≤–Ω–∞
    if (!phase2Active) return;
    
    playerHeartHP -= damage;
    
    // –ù–µ –¥–∞–µ–º HP —É–π—Ç–∏ –≤ –º–∏–Ω—É—Å
    if (playerHeartHP < 0) {
        playerHeartHP = 0;
    }
    
    // –≠—Ñ—Ñ–µ–∫—Ç —É—Ä–æ–Ω–∞
    if (playerHeart) {
        playerHeart.style.filter = 'brightness(3) saturate(0)';
        playerHeart.style.transform = 'scale(1.2)';
        
        setTimeout(() => {
            if (playerHeart && phase2Active) {
                playerHeart.style.filter = 'none';
                playerHeart.style.transform = 'scale(1)';
            }
        }, 200);
    }
    
    // –û–±–Ω–æ–≤–ª—è–µ–º UI
    let heartHPElement = document.getElementById('heartHP');
    if (heartHPElement) {
        heartHPElement.textContent = playerHeartHP;
    }
    
    //console.log(`–£—Ä–æ–Ω: ${damage}, HP: ${playerHeartHP}`);
    
    // –ú–ì–ù–û–í–ï–ù–ù–ê–Ø –æ—Å—Ç–∞–Ω–æ–≤–∫–∞ –ø—Ä–∏ —Å–º–µ—Ä—Ç–∏
    if (playerHeartHP <= 0) {
        //console.log('HP = 0! –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∏–≥—Ä—É!');
        
        // –ù–ï–ú–ï–î–õ–ï–ù–ù–û –æ—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∏–≥—Ä—É!
        phase2Active = false;
        
        // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∞—Ç–∞–∫–∏
        if (phase2AttackInterval) {
            clearInterval(phase2AttackInterval);
            phase2AttackInterval = null;
        }
        
        // –ë–ª–æ–∫–∏—Ä—É–µ–º –¥–≤–∏–∂–µ–Ω–∏–µ
        heartKeys = {};
        
        // –£–±–∏—Ä–∞–µ–º —Å–µ—Ä–¥—Ü–µ
        if (playerHeart) {
            playerHeart.style.opacity = '0.3';
            playerHeart.style.filter = 'grayscale(1)';
        }
        
        // Game Over —á–µ—Ä–µ–∑ —Å–µ–∫—É–Ω–¥—É
        setTimeout(() => {
            gameOver();
        }, 1000);
    }
}

