function loadAchievements() {
    let saved = localStorage.getItem('forsaken_achievements');
    if (saved) {
        let savedAchievements = JSON.parse(saved);
        // –û–±–Ω–æ–≤–ª—è–µ–º —Å—Ç–∞—Ç—É—Å –¥–æ—Å—Ç–∏–∂–µ–Ω–∏–π
        Object.keys(achievementsDatabase).forEach(category => {
            achievementsDatabase[category].forEach(achievement => {
                if (savedAchievements[achievement.id]) {
                    achievement.unlocked = true;
                }
            });
        });
    }

    displayAchievements();
    updateProgress();
}

