// –û–ë–ù–û–í–õ–ï–ù–ù–ê–Ø –§–£–ù–ö–¶–ò–Ø –ü–†–û–ì–†–ï–°–°–ê (–¢–û–õ–¨–ö–û –î–õ–Ø –°–ê–ô–¢–ê)
function updateProgress() {
    // –°–ß–ò–¢–ê–ï–ú –¢–û–õ–¨–ö–û –í–ò–î–ò–ú–´–ï –ê–ß–ò–í–ö–ò!
    let visibleSiteAchievements = achievementsDatabase.site.filter(achievement =>
        achievement.type !== 'hidden' || achievement.unlocked
    );

    let totalSiteAchievements = visibleSiteAchievements.length;
    let unlockedSiteAchievements = visibleSiteAchievements.filter(achievement =>
        achievement.unlocked
    ).length;

    let percentage = totalSiteAchievements > 0 ? Math.round((unlockedSiteAchievements / totalSiteAchievements) * 100) : 0;

    let progressElement = document.getElementById('totalProgress');
    let progressBarElement = document.getElementById('progressBar');

    if (progressElement) {
        progressElement.textContent = `${unlockedSiteAchievements}/${totalSiteAchievements} (${percentage}%)`;
    }

    if (progressBarElement) {
        progressBarElement.style.width = percentage + '%';
    }
}

