function interactWithObject(obj, roomId) {
    switch (obj.id) {
        case 'desk':
            findItem('key_server', '–ö–ª—é—á –æ—Ç —Å–µ—Ä–≤–µ—Ä–Ω–æ–π');
            break;
        case 'computer':
            showMessage('–ö–æ–º–ø—å—é—Ç–µ—Ä –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç. –ù—É–∂–Ω–æ —ç–ª–µ–∫—Ç—Ä–∏—á–µ—Å—Ç–≤–æ.');
            break;
        case 'door_server':
            if (hasItem('key_server')) {
                loadRoom('server');
            } else {
                showMessage('–î–≤–µ—Ä—å –∑–∞–ø–µ—Ä—Ç–∞. –ù—É–∂–µ–Ω –∫–ª—é—á.');
            }
            break;
        case 'door_office':
            loadRoom('office');
            break;
        case 'papers':
            findItem('document1', '–ó–∞–ø–∏—Å–∏ –ê–ª–µ–∫—Å–∞');
            break;
        case 'tapes':
            findItem('tape1', '–ö–∞—Å—Å–µ—Ç–∞ #001');
            break;
    }
}

function findItem(itemId, itemName) {
    if (!hasItem(itemId)) {
        playerInventory.push({ id: itemId, name: itemName });
        updateInventoryDisplay();
        showMessage(`–ù–∞–π–¥–µ–Ω–æ: ${itemName}`);
    } else {
        showMessage('–ó–¥–µ—Å—å –±–æ–ª—å—à–µ –Ω–∏—á–µ–≥–æ –Ω–µ—Ç.');
    }
}

function hasItem(itemId) {
    return playerInventory.some(item => item.id === itemId);
}

function updateInventoryDisplay() {
    let slots = document.getElementById('inventorySlots');
    if (!slots) return;

    slots.innerHTML = '';

    playerInventory.forEach(item => {
        let slot = document.createElement('div');
        slot.style.cssText = `
            width: 50px;
            height: 50px;
            background: #444;
            border: 2px solid #666;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 10px;
            color: #fff;
            cursor: pointer;
        `;
        slot.textContent = item.name.substring(0, 3);
        slot.title = item.name;
        slots.appendChild(slot);
    });
}

function showMessage(text) {
    let msg = document.createElement('div');
    msg.style.cssText = `
        position: fixed;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        background: rgba(0,0,0,0.9);
        color: #fff;
        padding: 20px;
        border: 2px solid #ff6b6b;
        border-radius: 10px;
        z-index: 9999;
        font-size: 16px;
        text-align: center;
    `;
    msg.textContent = text;
    document.body.appendChild(msg);

    setTimeout(() => msg.remove(), 2000);
}

function showTooltip(text) {
    let tooltip = document.getElementById('tooltip');
    if (!tooltip) {
        tooltip = document.createElement('div');
        tooltip.id = 'tooltip';
        tooltip.style.cssText = `
            position: fixed;
            background: rgba(0,0,0,0.8);
            color: #fff;
            padding: 5px 10px;
            border-radius: 5px;
            font-size: 12px;
            z-index: 9998;
            pointer-events: none;
        `;
        document.body.appendChild(tooltip);
    }
    tooltip.textContent = text;
    tooltip.style.display = 'block';

    // –ü–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä—É–µ–º —Ç—É–ª—Ç–∏–ø —Ä—è–¥–æ–º —Å –∫—É—Ä—Å–æ—Ä–æ–º
    document.addEventListener('mousemove', updateTooltipPosition);
}

function updateTooltipPosition(e) {
    let tooltip = document.getElementById('tooltip');
    if (tooltip && tooltip.style.display === 'block') {
        tooltip.style.left = (e.clientX + 10) + 'px';
        tooltip.style.top = (e.clientY - 30) + 'px';
    }
}

function hideTooltip() {
    let tooltip = document.getElementById('tooltip');
    if (tooltip) {
        tooltip.style.display = 'none';
        document.removeEventListener('mousemove', updateTooltipPosition);
    }
}

// –ú–û–ë–ò–õ–¨–ù–ê–Ø –ü–û–î–î–ï–†–ñ–ö–ê
function addMobileSupport() {
    const isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);

    if (isMobile) {
        document.body.classList.add('mobile-device');

        let spectreSection = document.getElementById('spectreSection');
        if (spectreSection) {
            let touchTimer;

            spectreSection.addEventListener('touchstart', function () {
                if (!spectreSection.classList.contains('locked')) {
                    touchTimer = setTimeout(() => {
                        spectreSection.classList.add('mobile-glitch');
                        setTimeout(() => {
                            spectreSection.classList.remove('mobile-glitch');
                        }, 1000);
                    }, 500);
                }
            });

            spectreSection.addEventListener('touchend', function () {
                clearTimeout(touchTimer);
            });
        }
    }
}

window.addEventListener('load', addMobileSupport);

function unlockSpectreSection() {
    let spectreSection = document.getElementById('spectreSection');
    if (spectreSection) {
        // –£–±–∏—Ä–∞–µ–º –∫–ª–∞—Å—Å "locked"
        spectreSection.classList.remove('locked');

        // –£–±–∏—Ä–∞–µ–º —Ü–µ–ø–∏ —Å –∞–Ω–∏–º–∞—Ü–∏–µ–π
        let chains = document.getElementById('spectreChains');
        if (chains) {
            chains.style.animation = 'chainBreak 1s ease-out forwards';
            setTimeout(() => {
                chains.style.display = 'none';
            }, 1000);
        }

        // –ú–µ–Ω—è–µ–º –æ–ø–∏—Å–∞–Ω–∏–µ
        let desc = spectreSection.querySelector('p');
        if (desc) {
            desc.textContent = '–ò—Å—Ç–∏–Ω–Ω–∞—è –∏—Å—Ç–æ—Ä–∏—è...';
            desc.style.color = '#ff6b6b';
        }

        // –ú–µ–Ω—è–µ–º –∑–∞–≥–æ–ª–æ–≤–æ–∫ –µ—Å–ª–∏ –µ—Å—Ç—å
        let title = spectreSection.querySelector('h3');
        if (title) {
            title.style.color = '#ff6b6b';
            title.style.textShadow = '0 0 10px #ff6b6b';
        }
    }

}
